CREATE OR REPLACE FUNCTION lisaa_tai_paivita_toimenpidekoodi
  (p_nimi              VARCHAR(255),
   p_koodi             VARCHAR(16),
   p_emo               INTEGER,
   p_taso              INTEGER,
   p_yksikko           VARCHAR(32),
   p_tuotenumero       INTEGER,
   p_historiakuva      BOOLEAN,
   p_kokonaishintainen BOOLEAN)
  RETURNS VOID AS $$
DECLARE
  toimenpiteen_id INTEGER;
BEGIN
  SELECT id
  INTO toimenpiteen_id
  FROM toimenpidekoodi
  WHERE nimi = p_nimi;
  BEGIN
    LOOP
      UPDATE toimenpidekoodi
      SET nimi            = p_nimi,
        koodi             = p_koodi,
        emo               = p_emo,
        taso              = p_taso,
        yksikko           = p_yksikko,
        tuotenumero       = p_tuotenumero,
        historiakuva      = p_historiakuva,
        kokonaishintainen = p_kokonaishintainen
      WHERE id = toimenpiteen_id;
      IF toimenpiteen_id IS NOT NULL
      THEN
        RETURN;
      END IF;
      BEGIN
        INSERT INTO toimenpidekoodi
        (nimi,
         koodi,
         emo,
         taso,
         yksikko,
         tuotenumero,
         historiakuva,
         kokonaishintainen)
        VALUES
          (p_nimi,
           p_koodi,
           p_emo,
           p_taso,
           p_yksikko,
           p_tuotenumero,
           p_historiakuva,
           p_kokonaishintainen);
        RETURN;
        EXCEPTION WHEN unique_violation
        THEN
      END;
    END LOOP;
  END;
END;
$$
LANGUAGE plpgsql;

