







-- Luodaan Kajaanintien sillalle 2 tarkastusta, jossa jälkimmäinen korjaa ensimmäisessä havaittuja ongelmi

INSERT
  INTO siltatarkastuskohde (siltatarkastus, kohde, tulos)
SELECT (SELECT id from siltatarkastus where tarkastusaika = '2014-04-08' AND urakka = (SELECT id FROM urakka WHERE nimi='Oulun alueurakka 2005-2010')) as tark, kohde, 'B' FROM generate_series(1,24) kohde;

